!function(){"use strict";let e=document.querySelector(".hamburger"),t=document.querySelector(".menu");e.addEventListener("click",(()=>{e.classList.toggle("hamburger__active"),t.classList.toggle("menu_active")})),t.addEventListener("click",(function(c){c.target.closest("li")&&(e.classList.remove("hamburger__active"),t.classList.remove("menu_active"))}))}();